package gameapp;

public class Point {
	private int x;
	private int y;
	Point(int x_index, int y_index) {
		  // TODO Auto-generated constructor stub
		  x = x_index;
		  y = y_index;
		}
		int getPointX() {
		  return x;
		}
		int getPointY() {
		  return y;
		}
		void setPointX(int x_index) {
		  x=x_index;
		}
		void setPointY(int y_index) {
		  y=y_index;
		}

}
